import React, { useState, useEffect, useCallback } from 'react';
import TransactionForm from '../components/TransactionForm';
import { transactionsAPI, categoriesAPI } from '../services/api';
import { formatCurrency, formatDate } from '../utils/formatters';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingTransaction, setEditingTransaction] = useState(null);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    categoryId: '',
    type: '',
    minAmount: '',
    maxAmount: ''
  });

  const fetchCategories = useCallback(async () => {
    try {
      const response = await categoriesAPI.getAll();
      setCategories(response.data);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–π:', error);
    }
  }, []);

  const fetchData = useCallback(async () => {
    setLoading(true);
    try {
      const params = {};
      if (filters.startDate) params.startDate = filters.startDate;
      if (filters.endDate) params.endDate = filters.endDate;
      if (filters.categoryId) params.categoryId = filters.categoryId;
      if (filters.type) params.type = filters.type;
      if (filters.minAmount) params.minAmount = filters.minAmount;
      if (filters.maxAmount) params.maxAmount = filters.maxAmount;

      const response = await transactionsAPI.getAll(params);
      setTransactions(response.data);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π:', error);
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    fetchData();
    fetchCategories();
  }, [fetchData, fetchCategories]);

  const handleAddTransaction = () => {
    console.log('–û—Ç–∫—Ä—ã—Ç–∏–µ —Ñ–æ—Ä–º—ã –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏');
    setEditingTransaction(null);
    setShowForm(true);
  };

  const handleEditTransaction = (transaction) => {
    console.log('–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏:', transaction);
    setEditingTransaction(transaction);
    setShowForm(true);
  };

  const handleDeleteTransaction = async (id) => {
    if (window.confirm('–£–¥–∞–ª–∏—Ç—å —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏—é?')) {
      try {
        await transactionsAPI.delete(id);
        fetchData();
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏:', error);
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏');
      }
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const applyFilters = () => {
    fetchData();
  };

  const clearFilters = () => {
    setFilters({
      startDate: '',
      endDate: '',
      categoryId: '',
      type: '',
      minAmount: '',
      maxAmount: ''
    });
    fetchData();
  };

  const handleSave = () => {
    console.log('–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏');
    setShowForm(false);
    setEditingTransaction(null);
    fetchData();
  };

  const handleCancel = () => {
    console.log('–û—Ç–º–µ–Ω–∞ —Ñ–æ—Ä–º—ã');
    setShowForm(false);
    setEditingTransaction(null);
  };

  // –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
  const totalIncome = transactions
    .filter(t => t.type === 1)
    .reduce((sum, t) => sum + t.amount, 0);

  const totalExpense = transactions
    .filter(t => t.type === 2)
    .reduce((sum, t) => sum + t.amount, 0);

  const balance = totalIncome - totalExpense;

  if (loading && transactions.length === 0 && !showForm) {
    return (
      <div style={styles.loading}>
        <div style={styles.spinner}></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1>–¢—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏</h1>
        {!showForm && (
          <button 
            onClick={handleAddTransaction}
            style={styles.addButton}
          >
            + –ù–æ–≤–∞—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏—è
          </button>
        )}
      </div>

      {/* –†–µ–∞–ª—å–Ω–∞—è —Ñ–æ—Ä–º–∞ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏ */}
      {showForm && (
        <div style={styles.formContainer}>
          <TransactionForm
            transaction={editingTransaction}
            categories={categories}
            onSave={handleSave}
            onCancel={handleCancel}
          />
        </div>
      )}

      {/* –û—Å–Ω–æ–≤–Ω–æ–π –∫–æ–Ω—Ç–µ–Ω—Ç –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ —Ñ–æ—Ä–º–∞ —Å–∫—Ä—ã—Ç–∞ */}
      {!showForm && (
        <>
          {/* –§–∏–ª—å—Ç—Ä—ã */}
          <div style={styles.filterCard}>
            <h3>–§–∏–ª—å—Ç—Ä—ã</h3>
            <div style={styles.filterGrid}>
              <div style={styles.filterGroup}>
                <label style={styles.filterLabel}>–î–∞—Ç–∞ –æ—Ç</label>
                <input
                  type="date"
                  name="startDate"
                  value={filters.startDate}
                  onChange={handleFilterChange}
                  style={styles.filterInput}
                />
              </div>

              <div style={styles.filterGroup}>
                <label style={styles.filterLabel}>–î–∞—Ç–∞ –¥–æ</label>
                <input
                  type="date"
                  name="endDate"
                  value={filters.endDate}
                  onChange={handleFilterChange}
                  style={styles.filterInput}
                />
              </div>

              <div style={styles.filterGroup}>
                <label style={styles.filterLabel}>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</label>
                <select
                  name="categoryId"
                  value={filters.categoryId}
                  onChange={handleFilterChange}
                  style={styles.filterInput}
                >
                  <option value="">–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</option>
                  {categories.map(cat => (
                    <option key={cat.id} value={cat.id}>
                      {cat.name}
                    </option>
                  ))}
                </select>
              </div>

              <div style={styles.filterGroup}>
                <label style={styles.filterLabel}>–¢–∏–ø</label>
                <select
                  name="type"
                  value={filters.type}
                  onChange={handleFilterChange}
                  style={styles.filterInput}
                >
                  <option value="">–í—Å–µ —Ç–∏–ø—ã</option>
                  <option value="1">–î–æ—Ö–æ–¥</option>
                  <option value="2">–†–∞—Å—Ö–æ–¥</option>
                </select>
              </div>

              <div style={styles.filterGroup}>
                <label style={styles.filterLabel}>–°—É–º–º–∞ –æ—Ç</label>
                <input
                  type="number"
                  name="minAmount"
                  value={filters.minAmount}
                  onChange={handleFilterChange}
                  style={styles.filterInput}
                  placeholder="–ú–∏–Ω–∏–º—É–º"
                />
              </div>

              <div style={styles.filterGroup}>
                <label style={styles.filterLabel}>–°—É–º–º–∞ –¥–æ</label>
                <input
                  type="number"
                  name="maxAmount"
                  value={filters.maxAmount}
                  onChange={handleFilterChange}
                  style={styles.filterInput}
                  placeholder="–ú–∞–∫—Å–∏–º—É–º"
                />
              </div>
            </div>

            <div style={styles.filterButtons}>
              <button onClick={applyFilters} style={styles.applyButton}>
                –ü—Ä–∏–º–µ–Ω–∏—Ç—å —Ñ–∏–ª—å—Ç—Ä—ã
              </button>
              <button onClick={clearFilters} style={styles.clearButton}>
                –û—á–∏—Å—Ç–∏—Ç—å —Ñ–∏–ª—å—Ç—Ä—ã
              </button>
            </div>
          </div>

          {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
          <div style={styles.stats}>
            <div style={styles.statItem}>
              <div style={styles.statLabel}>–î–æ—Ö–æ–¥—ã</div>
              <div style={styles.statValueIncome}>{formatCurrency(totalIncome)}</div>
            </div>
            <div style={styles.statItem}>
              <div style={styles.statLabel}>–†–∞—Å—Ö–æ–¥—ã</div>
              <div style={styles.statValueExpense}>{formatCurrency(totalExpense)}</div>
            </div>
            <div style={styles.statItem}>
              <div style={styles.statLabel}>–ë–∞–ª–∞–Ω—Å</div>
              <div style={{
                ...styles.statValueBalance,
                color: balance >= 0 ? '#2e7d32' : '#c62828'
              }}>
                {formatCurrency(balance)}
              </div>
            </div>
          </div>

          {/* –°–ø–∏—Å–æ–∫ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π */}
          <div style={styles.tableContainer}>
            {loading ? (
              <div style={styles.loadingTable}>
                <div style={styles.spinner}></div>
                <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>
              </div>
            ) : transactions.length === 0 ? (
              <div style={styles.emptyState}>
                <p>–¢—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</p>
                <button 
                  onClick={handleAddTransaction}
                  style={styles.emptyButton}
                >
                  –î–æ–±–∞–≤–∏—Ç—å –ø–µ—Ä–≤—É—é —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏—é
                </button>
              </div>
            ) : (
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th style={styles.tableTh}>–î–∞—Ç–∞</th>
                    <th style={styles.tableTh}>–û–ø–∏—Å–∞–Ω–∏–µ</th>
                    <th style={styles.tableTh}>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th>
                    <th style={styles.tableTh}>–¢–∏–ø</th>
                    <th style={styles.tableTh} align="right">–°—É–º–º–∞</th>
                    <th style={styles.tableTh}>–î–µ–π—Å—Ç–≤–∏—è</th>
                  </tr>
                </thead>
                <tbody>
                  {transactions.map(transaction => (
                    <tr key={transaction.id}>
                      <td style={styles.tableTd}>{formatDate(transaction.date)}</td>
                      <td style={styles.tableTd}>{transaction.description}</td>
                      <td style={styles.tableTd}>{transaction.categoryName}</td>
                      <td style={styles.tableTd}>
                        <span style={{
                          ...styles.typeBadge,
                          backgroundColor: transaction.type === 1 ? '#e8f5e9' : '#ffebee',
                          color: transaction.type === 1 ? '#2e7d32' : '#c62828'
                        }}>
                          {transaction.type === 1 ? '–î–æ—Ö–æ–¥' : '–†–∞—Å—Ö–æ–¥'}
                        </span>
                      </td>
                      <td style={{
                        ...styles.tableTd,
                        fontWeight: 'bold',
                        color: transaction.type === 1 ? '#2e7d32' : '#c62828',
                        textAlign: 'right'
                      }}>
                        {transaction.type === 1 ? '+' : '-'}
                        {formatCurrency(transaction.amount)}
                      </td>
                      <td style={styles.tableTd}>
                        <div style={styles.actionButtons}>
                          <button 
                            onClick={() => handleEditTransaction(transaction)}
                            style={styles.editButton}
                          >
                            ‚úèÔ∏è
                          </button>
                          <button 
                            onClick={() => handleDeleteTransaction(transaction.id)}
                            style={styles.deleteButton}
                          >
                            üóëÔ∏è
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </>
      )}
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '30px',
