        <div style={styles.formContainer}>
          <TransactionForm
            transaction={editingTransaction}
            categories={categories} // –î–æ–±–∞–≤–ª—è–µ–º –ø–µ—Ä–µ–¥–∞—á—É –∫–∞—Ç–µ–≥–æ—Ä–∏–π
            onSave={handleSave}
            onCancel={handleCancel}
          />
        </div>
      )}

