              </button>
              <button onClick={clearFilters} style={styles.clearButton}>
                –û—á–∏—Å—Ç–∏—Ç—å —Ñ–∏–ª—å—Ç—Ä—ã
              </button>
            </div>
          </div>

          {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
          {!loading && (
            <div style={styles.stats}>
              <div style={styles.statItem}>
                <div style={styles.statLabel}>–î–æ—Ö–æ–¥—ã</div>
                <div style={styles.statValueIncome}>{formatCurrency(totalIncome)}</div>
              </div>
              <div style={styles.statItem}>
                <div style={styles.statLabel}>–†–∞—Å—Ö–æ–¥—ã</div>
                <div style={styles.statValueExpense}>{formatCurrency(totalExpense)}</div>
              </div>
              <div style={styles.statItem}>
                <div style={styles.statLabel}>–ë–∞–ª–∞–Ω—Å</div>
                <div style={{
                  ...styles.statValueBalance,
                  color: balance >= 0 ? '#2e7d32' : '#c62828'
                }}>
                  {formatCurrency(balance)}
                </div>
              </div>
            </div>
          )}

          {/* –°–ø–∏—Å–æ–∫ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π */}
          <div style={styles.tableContainer}>
            {loading ? (
              <div style={styles.loadingTable}>
                <div style={styles.spinner}></div>
                <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>
              </div>
            ) : transactions.length === 0 ? (
              <div style={styles.emptyState}>
                <p>–¢—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</p>
                <button 
                  onClick={handleAddTransaction}
                  style={styles.emptyButton}
                >
                  –î–æ–±–∞–≤–∏—Ç—å –ø–µ—Ä–≤—É—é —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏—é
                </button>
              </div>
            ) : (
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th style={styles.tableTh}>–î–∞—Ç–∞</th>
                    <th style={styles.tableTh}>–û–ø–∏—Å–∞–Ω–∏–µ</th>
                    <th style={styles.tableTh}>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th>
                    <th style={styles.tableTh}>–¢–∏–ø</th>
                    <th style={styles.tableTh} align="right">–°—É–º–º–∞</th>
                    <th style={styles.tableTh}>–î–µ–π—Å—Ç–≤–∏—è</th>
                  </tr>
                </thead>
                <tbody>
                  {transactions.map(transaction => (
                    <tr key={transaction.id}>
                      <td style={styles.tableTd}>{formatDate(transaction.date)}</td>
                      <td style={styles.tableTd}>{transaction.description}</td>
                      <td style={styles.tableTd}>{transaction.categoryName}</td>
                      <td style={styles.tableTd}>
                        <span style={{
                          ...styles.typeBadge,
                          backgroundColor: transaction.type === 1 ? '#e8f5e9' : '#ffebee',
                          color: transaction.type === 1 ? '#2e7d32' : '#c62828'
                        }}>
                          {transaction.type === 1 ? '–î–æ—Ö–æ–¥' : '–†–∞—Å—Ö–æ–¥'}
                        </span>
                      </td>
                      <td style={styles.tableTd} align="right" style={{
                        fontWeight: 'bold',
                        color: transaction.type === 1 ? '#2e7d32' : '#c62828'
                      }}>
                        {transaction.type === 1 ? '+' : '-'}
                        {formatCurrency(transaction.amount)}
                      </td>
                      <td style={styles.tableTd}>
                        <div style={styles.actionButtons}>
                          <button 
                            onClick={() => handleEditTransaction(transaction)}
                            style={styles.editButton}
                          >
                            ‚úèÔ∏è
                          </button>
                          <button 
                            onClick={() => handleDeleteTransaction(transaction.id)}
                            style={styles.deleteButton}
                          >
                            üóëÔ∏è
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </>
      )}
    </div>
  );
};

