import React, { useState, useEffect } from 'react';
import { transactionsAPI, categoriesAPI } from '../services/api';
import { formatCurrency, formatDate } from '../utils/formatters';

// Создаем простую форму прямо здесь для теста
const SimpleTransactionForm = ({ onSave, onCancel }) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [type, setType] = useState('2');
  const [categoryId, setCategoryId] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Форма отправлена:', { description, amount, type, categoryId });
    onSave();
  };

  return (
    <div style={{
      backgroundColor: 'white',
      padding: '30px',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
      marginBottom: '30px'
    }}>
      <h2>Новая транзакция</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Описание
          </label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '16px'
            }}
            placeholder="Введите описание"
            required
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Сумма
          </label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '16px'
            }}
            placeholder="0.00"
            min="0"
            step="0.01"
            required
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Тип
          </label>
          <select
            value={type}
            onChange={(e) => setType(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '16px',
              backgroundColor: 'white'
            }}
          >
            <option value="1">Доход</option>
            <option value="2">Расход</option>
          </select>
        </div>

        <div style={{ marginBottom: '25px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Дата
          </label>
          <input
            type="date"
            style={{
              width: '100%',
              padding: '10px',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '16px'
            }}
            required
          />
        </div>

        <div style={{ display: 'flex', gap: '15px' }}>
          <button
            type="submit"
            style={{
              backgroundColor: '#1976d2',
              color: 'white',
              border: 'none',
              padding: '12px 24px',
              borderRadius: '6px',
              fontSize: '16px',
              cursor: 'pointer',
              fontWeight: 'bold',
              flex: 1
            }}
          >
            Сохранить
          </button>
          <button
            type="button"
            onClick={onCancel}
            style={{
              backgroundColor: '#f5f5f5',
              color: '#333',
              border: '1px solid #ddd',
              padding: '12px 24px',
              borderRadius: '6px',
              fontSize: '16px',
              cursor: 'pointer',
              fontWeight: 'bold',
              flex: 1
            }}
          >
            Отмена
          </button>
        </div>
      </form>
    </div>
  );
};

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    categoryId: '',
    type: '',
    minAmount: '',
    maxAmount: ''
  });

  useEffect(() => {
    fetchData();
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await categoriesAPI.getAll();
      setCategories(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке категорий:', error);
    }
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const params = {};
      if (filters.startDate) params.startDate = filters.startDate;
      if (filters.endDate) params.endDate = filters.endDate;
      if (filters.categoryId) params.categoryId = filters.categoryId;
      if (filters.type) params.type = filters.type;
      if (filters.minAmount) params.minAmount = filters.minAmount;
      if (filters.maxAmount) params.maxAmount = filters.maxAmount;

      const response = await transactionsAPI.getAll(params);
      setTransactions(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке транзакций:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddTransaction = () => {
    console.log('handleAddTransaction вызван');
    setShowForm(true);
  };

  const handleEditTransaction = (transaction) => {
    console.log('Редактирование транзакции:', transaction);
    setShowForm(true);
  };

  const handleDeleteTransaction = async (id) => {
    if (window.confirm('Удалить транзакцию?')) {
      try {
        await transactionsAPI.delete(id);
        fetchData();
      } catch (error) {
        console.error('Ошибка при удалении транзакции:', error);
        alert('Ошибка при удалении транзакции');
      }
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const applyFilters = () => {
    fetchData();
  };

  const clearFilters = () => {
    setFilters({
      startDate: '',
      endDate: '',
      categoryId: '',
      type: '',
      minAmount: '',
      maxAmount: ''
    });
    fetchData();
  };

  const handleSave = () => {
    console.log('handleSave вызван');
    setShowForm(false);
    fetchData();
  };

  const handleCancel = () => {
    console.log('handleCancel вызван');
    setShowForm(false);
  };

  const totalIncome = transactions
    .filter(t => t.type === 1)
    .reduce((sum, t) => sum + t.amount, 0);

  const totalExpense = transactions
    .filter(t => t.type === 2)
    .reduce((sum, t) => sum + t.amount, 0);

  const balance = totalIncome - totalExpense;

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Транзакции</h1>
        {!showForm && (
          <button 
            onClick={handleAddTransaction}
            style={{
              backgroundColor: '#1976d2',
              color: 'white',
              border: 'none',
              padding: '12px 24px',
              borderRadius: '6px',
              fontSize: '16px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            + Новая транзакция
          </button>
        )}
      </div>

      {/* Показываем простую форму */}
      {showForm && (
        <SimpleTransactionForm onSave={handleSave} onCancel={handleCancel} />
      )}

      {/* Основной контент показываем только если форма скрыта */}
      {!showForm && (
        <>
          {/* Фильтры */}
          <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '8px', marginBottom: '20px' }}>
            <h3>Фильтры</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '15px', marginBottom: '15px' }}>
              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>Дата от</label>
                <input
                  type="date"
                  name="startDate"
                  value={filters.startDate}
                  onChange={handleFilterChange}
                  style={{ width: '100%', padding: '8px', border: '1px solid #ddd', borderRadius: '4px' }}
                />
              </div>

              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>Дата до</label>
                <input
                  type="date"
                  name="endDate"
                  value={filters.endDate}
                  onChange={handleFilterChange}
                  style={{ width: '100%', padding: '8px', border: '1px solid #ddd', borderRadius: '4px' }}
                />
              </div>

              <div>
                <label style={{ display: 'block', marginBottom: '5px' }}>Категория</label>
                <select
                  name="categoryId"
                  value={filters.categoryId}
                  onChange={handleFilterChange}
                  style={{ width: '100%', padding: '8px', border: '1px solid #ddd', borderRadius: '4px' }}
                >
                  <option value="">Все категории</option>
                  {categories.map(cat => (
                    <option key={cat.id} value={cat.id}>{cat.name}</option>
                  ))}
                </select>
              </div>
            </div>

            <div style={{ display: 'flex', gap: '10px' }}>
              <button onClick={applyFilters} style={{ backgroundColor: '#1976d2', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '4px', cursor: 'pointer' }}>
                Применить фильтры
              </button>
              <button onClick={clearFilters} style={{ backgroundColor: '#f5f5f5', color: '#333', border: '1px solid #ddd', padding: '8px 16px', borderRadius: '4px', cursor: 'pointer' }}>
                Очистить фильтры
              </button>
            </div>
          </div>

          {/* Статистика */}
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px', backgroundColor: 'white', padding: '20px', borderRadius: '8px' }}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '14px', color: '#666', marginBottom: '5px' }}>Доходы</div>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#2e7d32' }}>{formatCurrency(totalIncome)}</div>
            </div>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '14px', color: '#666', marginBottom: '5px' }}>Расходы</div>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#c62828' }}>{formatCurrency(totalExpense)}</div>
            </div>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '14px', color: '#666', marginBottom: '5px' }}>Баланс</div>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: balance >= 0 ? '#2e7d32' : '#c62828' }}>
                {formatCurrency(balance)}
              </div>
            </div>
          </div>

          {/* Список транзакций */}
          <div style={{ backgroundColor: 'white', borderRadius: '8px', overflow: 'auto' }}>
            {loading ? (
              <div style={{ textAlign: 'center', padding: '40px' }}>
                <div style={{ display: 'inline-block', width: '40px', height: '40px', border: '4px solid #f3f3f3', borderTop: '4px solid #1976d2', borderRadius: '50%', animation: 'spin 1s linear infinite' }}></div>
                <p>Загрузка...</p>
              </div>
            ) : transactions.length === 0 ? (
              <div style={{ textAlign: 'center', padding: '40px 20px' }}>
                <p>Транзакции не найдены</p>
                <button 
                  onClick={handleAddTransaction}
                  style={{ backgroundColor: '#1976d2', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '4px', marginTop: '10px', cursor: 'pointer' }}
                >
                  Добавить первую транзакцию
                </button>
              </div>
            ) : (
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr>
                    <th style={{ backgroundColor: '#f5f5f5', padding: '12px', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Дата</th>
                    <th style={{ backgroundColor: '#f5f5f5', padding: '12px', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Описание</th>
                    <th style={{ backgroundColor: '#f5f5f5', padding: '12px', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Категория</th>
                    <th style={{ backgroundColor: '#f5f5f5', padding: '12px', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Тип</th>
                    <th style={{ backgroundColor: '#f5f5f5', padding: '12px', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Сумма</th>
                    <th style={{ backgroundColor: '#f5f5f5', padding: '12px', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Действия</th>
                  </tr>
                </thead>
                <tbody>
                  {transactions.map(transaction => (
                    <tr key={transaction.id}>
                      <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{formatDate(transaction.date)}</td>
                      <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{transaction.description}</td>
                      <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{transaction.categoryName}</td>
                      <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>
                        <span style={{
                          padding: '4px 8px',
                          borderRadius: '4px',
                          fontSize: '12px',
                          fontWeight: 'bold',
                          backgroundColor: transaction.type === 1 ? '#e8f5e9' : '#ffebee',
                          color: transaction.type === 1 ? '#2e7d32' : '#c62828'
                        }}>
                          {transaction.type === 1 ? 'Доход' : 'Расход'}
                        </span>
                      </td>
                      <td style={{ padding: '12px', borderBottom: '1px solid #eee', fontWeight: 'bold', color: transaction.type === 1 ? '#2e7d32' : '#c62828' }}>
                        {transaction.type === 1 ? '+' : '-'}{formatCurrency(transaction.amount)}
                      </td>
                      <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>
                        <div style={{ display: 'flex', gap: '5px' }}>
                          <button 
                            onClick={() => handleEditTransaction(transaction)}
                            style={{ backgroundColor: '#e3f2fd', border: 'none', padding: '5px 10px', borderRadius: '4px', cursor: 'pointer' }}
                          >
                            ✏️
                          </button>
                          <button 
                            onClick={() => handleDeleteTransaction(transaction.id)}
                            style={{ backgroundColor: '#ffebee', border: 'none', padding: '5px 10px', borderRadius: '4px', cursor: 'pointer' }}
                          >
                            🗑️
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Transactions;
