import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { transactionsAPI, categoriesAPI } from '../services/api';

const TransactionForm = ({ transaction, onSave, onCancel }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [formData, setFormData] = useState({
    amount: '',
    description: '',
    date: new Date().toISOString().split('T')[0],
    categoryId: '',
    type: '2' // По умолчанию расход
  });
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchCategories();

    // Проверяем, есть ли параметр edit в URL
    const queryParams = new URLSearchParams(location.search);
    const editId = queryParams.get('edit');

    if (editId) {
      fetchTransaction(editId);
    } else if (transaction) {
      // Если передали транзакцию как пропс
      setFormData({
        amount: transaction.amount.toString(),
        description: transaction.description,
        date: transaction.date.split('T')[0],
        categoryId: transaction.categoryId.toString(),
        type: transaction.type.toString()
      });
    }
  }, [transaction, location.search]);

  const fetchCategories = async () => {
    try {
      const response = await categoriesAPI.getAll();
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchTransaction = async (id) => {
    try {
      setLoading(true);
      const response = await transactionsAPI.getById(id);
      const transaction = response.data;
      setFormData({
        amount: transaction.amount.toString(),
        description: transaction.description,
        date: transaction.date.split('T')[0],
        categoryId: transaction.categoryId.toString(),
        type: transaction.type.toString()
      });
    } catch (error) {
      console.error('Error fetching transaction:', error);
      alert('Ошибка при загрузке транзакции');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Очищаем ошибку при изменении поля
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.amount || parseFloat(formData.amount) <= 0) {
      newErrors.amount = 'Введите корректную сумму';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Введите описание';
    }

    if (!formData.date) {
      newErrors.date = 'Выберите дату';
    }

    if (!formData.categoryId) {
      newErrors.categoryId = 'Выберите категорию';
    }

    if (!formData.type) {
      newErrors.type = 'Выберите тип';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const transactionData = {
        amount: parseFloat(formData.amount),
        description: formData.description.trim(),
        date: formData.date,
        categoryId: parseInt(formData.categoryId),
        type: parseInt(formData.type)
      };

      if (transaction?.id) {
        // Редактирование существующей транзакции
        await transactionsAPI.update(transaction.id, transactionData);
      } else {
        // Создание новой транзакции
        await transactionsAPI.create(transactionData);
      }

      // Закрываем форму и обновляем список
      if (onSave) {
        onSave();
      } else {
        // Если форма открыта как отдельная страница
        navigate(-1);
      }

    } catch (error) {
      console.error('Error saving transaction:', error);
      let errorMessage = 'Ошибка при сохранении транзакции';

      if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      }

      alert(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (onCancel) {
      onCancel();
    } else {
      navigate(-1);
    }
  };

  if (loading && transaction?.id) {
    return (
      <div style={styles.loading}>
        <div style={styles.spinner}></div>
        <p>Загрузка...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Тип транзакции *</label>
          <div style={styles.typeButtons}>
            <button
              type="button"
              onClick={() => handleChange({ target: { name: 'type', value: '1' } })}
              style={{
                ...styles.typeButton,
                ...(formData.type === '1' ? styles.typeButtonIncomeActive : styles.typeButtonIncome)
              }}
            >
              Доход
            </button>
            <button
              type="button"
              onClick={() => handleChange({ target: { name: 'type', value: '2' } })}
              style={{
                ...styles.typeButton,
                ...(formData.type === '2' ? styles.typeButtonExpenseActive : styles.typeButtonExpense)
              }}
            >
              Расход
            </button>
          </div>
          {errors.type && <span style={styles.error}>{errors.type}</span>}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Сумма *</label>
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            style={styles.input}
            placeholder="0.00"
            step="0.01"
            min="0"
          />
          {errors.amount && <span style={styles.error}>{errors.amount}</span>}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Описание *</label>
          <input
            type="text"
            name="description"
            value={formData.description}
            onChange={handleChange}
            style={styles.input}
            placeholder="На что потратили или откуда доход"
            maxLength="200"
          />
          {errors.description && <span style={styles.error}>{errors.description}</span>}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Дата *</label>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            style={styles.input}
          />
          {errors.date && <span style={styles.error}>{errors.date}</span>}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Категория *</label>
          <select
            name="categoryId"
            value={formData.categoryId}
            onChange={handleChange}
            style={styles.select}
          >
            <option value="">Выберите категорию</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          {errors.categoryId && <span style={styles.error}>{errors.categoryId}</span>}
        </div>

        <div style={styles.buttonGroup}>
          <button
            type="button"
            onClick={handleCancel}
            style={styles.cancelButton}
            disabled={loading}
          >
            Отмена
          </button>
          <button
            type="submit"
            style={styles.submitButton}
            disabled={loading}
          >
            {loading ? 'Сохранение...' : (transaction?.id ? 'Сохранить изменения' : 'Создать транзакцию')}
          </button>
        </div>
      </form>
    </div>
  );
};

